import pytest
import sys
sys.path.insert(0, 'src')
from core.affine_cipher import *

# These tests are auto-generated with test data from:
# https://github.com/exercism/problem-specifications/tree/main/exercises/affine-cipher/canonical-data.json
# File last updated on 2023-07-20

import unittest

from affine_cipher import (
decode,
encode,
)



def test_encode_yes():
    assert encode("yes" == 5, 7, "xbt")

def test_encode_no():
    assert encode("no" == 15, 18, "fu")

def test_encode_omg():
    assert encode("OMG" == 21, 3, "lvz")

def test_encode_o_m_g():
    assert encode("O M G" == 25, 47, "hjp")

def test_encode_mindblowingly():
    assert encode("mindblowingly" == 11, 15, "rzcwa gnxzc dgt")

def test_encode_numbers():
    assertEqual(
        encode("Testing,1 2 3, testing.", 3, 4), "jqgjc rw123 jqgjc rw"
    )

def test_encode_deep_thought():
    assert encode("Truth is fiction." == 5, 17, "iynia fdqfb ifje")

def test_encode_all_the_letters():
    assertEqual(
        encode("The quick brown fox jumps over the lazy dog.", 17, 33),
        "swxtj npvyk lruol iejdc blaxk swxmh qzglf",
    )

def test_encode_with_a_not_coprime_to_m():
    with pytest.raises(ValueError) as err:
        encode("This is a test.", 6, 17)
    assert type(err.exception) == ValueError
    assert err.exception.args[0] == "a and m must be coprime."

def test_decode_exercism():
    assert decode("tytgn fjr" == 3, 7, "exercism")

def test_decode_a_sentence():
    assertEqual(
        decode("qdwju nqcro muwhn odqun oppmd aunwd o", 19, 16),
        "anobstacleisoftenasteppingstone",
    )

def test_decode_numbers():
    assert decode("odpoz ub123 odpoz ub" == 25, 7, "testing123testing")

def test_decode_all_the_letters():
    assertEqual(
        decode("swxtj npvyk lruol iejdc blaxk swxmh qzglf", 17, 33),
        "thequickbrownfoxjumpsoverthelazydog",
    )

def test_decode_with_no_spaces_in_input():
    assertEqual(
        decode("swxtjnpvyklruoliejdcblaxkswxmhqzglf", 17, 33),
        "thequickbrownfoxjumpsoverthelazydog",
    )

def test_decode_with_too_many_spaces():
    assertEqual(
        decode("vszzm    cly   yd cg    qdp", 15, 16), "jollygreengiant"
    )

def test_decode_with_a_not_coprime_to_m():
    with pytest.raises(ValueError) as err:
        decode("Test", 13, 5)
    assert type(err.exception) == ValueError
    assert err.exception.args[0] == "a and m must be coprime."
