import pytest
import sys
sys.path.insert(0, 'src')
from core.atbash_cipher import *

# These tests are auto-generated with test data from:
# https://github.com/exercism/problem-specifications/tree/main/exercises/atbash-cipher/canonical-data.json
# File last updated on 2023-07-20

import unittest

from atbash_cipher import (
decode,
encode,
)



def test_encode_yes():
    assert encode("yes") == "bvh"

def test_encode_no():
    assert encode("no") == "ml"

def test_encode_omg():
    assert encode("OMG") == "lnt"

def test_encode_spaces():
    assert encode("O M G") == "lnt"

def test_encode_mindblowingly():
    assert encode("mindblowingly") == "nrmwy oldrm tob"

def test_encode_numbers():
    assert encode("Testing == 1 2 3, testing.", "gvhgr mt123 gvhgr mt")

def test_encode_deep_thought():
    assert encode("Truth is fiction.") == "gifgs rhurx grlm"

def test_encode_all_the_letters():
    assertEqual(
        encode("The quick brown fox jumps over the lazy dog."),
        "gsvjf rxpyi ldmul cqfnk hlevi gsvoz abwlt",
    )

def test_decode_exercism():
    assert decode("vcvix rhn") == "exercism"

def test_decode_a_sentence():
    assertEqual(
        decode("zmlyh gzxov rhlug vmzhg vkkrm thglm v"),
        "anobstacleisoftenasteppingstone",
    )

def test_decode_numbers():
    assert decode("gvhgr mt123 gvhgr mt") == "testing123testing"

def test_decode_all_the_letters():
    assertEqual(
        decode("gsvjf rxpyi ldmul cqfnk hlevi gsvoz abwlt"),
        "thequickbrownfoxjumpsoverthelazydog",
    )

def test_decode_with_too_many_spaces():
    assert decode("vc vix    r hn") == "exercism"

def test_decode_with_no_spaces():
    assertEqual(
        decode("zmlyhgzxovrhlugvmzhgvkkrmthglmv"), "anobstacleisoftenasteppingstone"
    )
