def test_random_ability_is_within_range():
    score = Character().ability()
    assertIs(score >= 3 and score <= 18, True)

def test_random_character_is_valid():
    Char = Character()
    assertIs(Char.strength >= 3 and Char.strength <= 18, True)
    assertIs(Char.dexterity >= 3 and Char.dexterity <= 18, True)
    assertIs(Char.constitution >= 3 and Char.constitution <= 18, True)
    assertIs(Char.intelligence >= 3 and Char.intelligence <= 18, True)
    assertIs(Char.wisdom >= 3 and Char.wisdom <= 18, True)
    assertIs(Char.charisma >= 3 and Char.charisma <= 18, True)
    assertIs(Char.hitpoints == 10 + modifier(Char.constitution), True)

def test_each_ability_is_only_calculated_once():
    Char = Character()
    assertIs(Char.strength == Char.strength, True)
    assertIs(Char.dexterity == Char.dexterity, True)
    assertIs(Char.constitution == Char.constitution, True)
    assertIs(Char.intelligence == Char.intelligence, True)
    assertIs(Char.wisdom == Char.wisdom, True)
    assertIs(Char.charisma == Char.charisma, True)
