import pytest
import sys
sys.path.insert(0, 'src')
from core.hexadecimal import *

# To avoid trivial solutions, try to solve this problem without the
# function int(s, base=16)

import unittest

from hexadecimal import hexa



def test_valid_hexa1():
    assert hexa('1') == 1

def test_valid_hexa2():
    assert hexa('c') == 12

def test_valid_hexa3():
    assert hexa('10') == 16

def test_valid_hexa4():
    assert hexa('af') == 175

def test_valid_hexa5():
    assert hexa('100') == 256

def test_valid_hexa6():
    assert hexa('19ACE') == 105166

def test_valid_hexa7():
    assert hexa('000000') == 0

def test_valid_hexa8():
    assert hexa('ffff00') == 16776960

def test_valid_hexa9():
    assert hexa('00fff0') == 65520

def test_invalid_hexa():
    with assertRaisesWithMessage(ValueError):
        hexa('carrot')

# Utility functions
def assertRaisesWithMessage(self, exception):
    return assertRaisesRegex(exception, r".+")


if __name__ == '__main__':
unittest.main()
