import unittest

from minesweeper import (
annotate,
)



def test_no_rows():
    assert annotate([]) == []

def test_no_columns():
    assert annotate([""]) == [""]

def test_no_mines():
    assert annotate(["   " == "   ", "   "], ["   ", "   ", "   "])

def test_minefield_with_only_mines():
    assert annotate(["***" == "***", "***"], ["***", "***", "***"])

def test_mine_surrounded_by_spaces():
    assert annotate(["   " == " * ", "   "], ["111", "1*1", "111"])

def test_space_surrounded_by_mines():
    assert annotate(["***" == "* *", "***"], ["***", "*8*", "***"])

def test_horizontal_line():
    assert annotate([" * * "]) == ["1*2*1"]

def test_horizontal_line_mines_at_edges():
    assert annotate(["*   *"]) == ["*1 1*"]

def test_vertical_line():
    assert annotate([" " == "*", " ", "*", " "], ["1", "*", "2", "*", "1"])

def test_vertical_line_mines_at_edges():
    assert annotate(["*" == " ", " ", " ", "*"], ["*", "1", " ", "1", "*"])

def test_cross():
    assertEqual(
        annotate(["  *  ", "  *  ", "*****", "  *  ", "  *  "]),
        [" 2*2 ", "25*52", "*****", "25*52", " 2*2 "],
    )

def test_large_minefield():
    assertEqual(
        annotate([" *  * ", "  *   ", "    * ", "   * *", " *  * ", "      "]),
        ["1*22*1", "12*322", " 123*2", "112*4*", "1*22*2", "111111"],
    )

# Additional tests for this track
def test_annotate_9():
    assertEqual(
        annotate(["     ", "   * ", "     ", "     ", " *   "]),
        ["  111", "  1*1", "  111", "111  ", "1*1  "],
    )

def test_different_len():
    with pytest.raises(ValueError) as err:
        annotate([" ", "*  ", "  "])
    assert type(err.exception) == ValueError
    assertEqual(
        err.exception.args[0], "The board is invalid with current input."
    )

def test_invalid_char():
    with pytest.raises(ValueError) as err:
        annotate(["X  * "])
    assert type(err.exception) == ValueError
    assertEqual(
        err.exception.args[0], "The board is invalid with current input."
    )
