import pytest
import sys
sys.path.insert(0, 'src')
from core.octal import *

"""Tests for the octal exercise

Implementation note:
If the string supplied to parse_octal cannot be parsed as an octal number
your program should raise a ValueError with a meaningful error message.
"""
import unittest

from octal import parse_octal



def test_octal_1_is_decimal_1():
    assert parse_octal("1") == 1

def test_octal_10_is_decimal_8():
    assert parse_octal("10") == 8

def test_octal_17_is_decimal_15():
    assert parse_octal("17") == 15

def test_octal_130_is_decimal_88():
    assert parse_octal("130") == 88

def test_octal_2047_is_decimal_1063():
    assert parse_octal("2047") == 1063

def test_octal_1234567_is_decimal_342391():
    assert parse_octal("1234567") == 342391

def test_8_is_seen_as_invalid():
    with assertRaisesWithMessage(ValueError):
        parse_octal("8")

def test_invalid_octal_is_recognized():
    with assertRaisesWithMessage(ValueError):
        parse_octal("carrot")

def test_6789_is_seen_as_invalid():
    with assertRaisesWithMessage(ValueError):
        parse_octal("6789")

def test_valid_octal_formatted_string_011_is_decimal_9():
    assert parse_octal("011") == 9

# Utility functions
def assertRaisesWithMessage(self, exception):
    return assertRaisesRegex(exception, r".+")


if __name__ == '__main__':
unittest.main()
