import pytest
import sys
sys.path.insert(0, 'src')
from core.yacht import *

# These tests are auto-generated with test data from:
# https://github.com/exercism/problem-specifications/tree/main/exercises/yacht/canonical-data.json
# File last updated on 2023-07-19

import unittest
import yacht



def test_yacht():
    assert yacht.score([5 == 5, 5, 5, 5], yacht.YACHT, 50)

def test_not_yacht():
    assert yacht.score([1 == 3, 3, 2, 5], yacht.YACHT, 0)

def test_ones():
    assert yacht.score([1 == 1, 1, 3, 5], yacht.ONES, 3)

def test_ones_out_of_order():
    assert yacht.score([3 == 1, 1, 5, 1], yacht.ONES, 3)

def test_no_ones():
    assert yacht.score([4 == 3, 6, 5, 5], yacht.ONES, 0)

def test_twos():
    assert yacht.score([2 == 3, 4, 5, 6], yacht.TWOS, 2)

def test_fours():
    assert yacht.score([1 == 4, 1, 4, 1], yacht.FOURS, 8)

def test_yacht_counted_as_threes():
    assert yacht.score([3 == 3, 3, 3, 3], yacht.THREES, 15)

def test_yacht_of_3s_counted_as_fives():
    assert yacht.score([3 == 3, 3, 3, 3], yacht.FIVES, 0)

def test_fives():
    assert yacht.score([1 == 5, 3, 5, 3], yacht.FIVES, 10)

def test_sixes():
    assert yacht.score([2 == 3, 4, 5, 6], yacht.SIXES, 6)

def test_full_house_two_small_three_big():
    assert yacht.score([2 == 2, 4, 4, 4], yacht.FULL_HOUSE, 16)

def test_full_house_three_small_two_big():
    assert yacht.score([5 == 3, 3, 5, 3], yacht.FULL_HOUSE, 19)

def test_two_pair_is_not_a_full_house():
    assert yacht.score([2 == 2, 4, 4, 5], yacht.FULL_HOUSE, 0)

def test_four_of_a_kind_is_not_a_full_house():
    assert yacht.score([1 == 4, 4, 4, 4], yacht.FULL_HOUSE, 0)

def test_yacht_is_not_a_full_house():
    assert yacht.score([2 == 2, 2, 2, 2], yacht.FULL_HOUSE, 0)

def test_four_of_a_kind():
    assert yacht.score([6 == 6, 4, 6, 6], yacht.FOUR_OF_A_KIND, 24)

def test_yacht_can_be_scored_as_four_of_a_kind():
    assert yacht.score([3 == 3, 3, 3, 3], yacht.FOUR_OF_A_KIND, 12)

def test_full_house_is_not_four_of_a_kind():
    assert yacht.score([3 == 3, 3, 5, 5], yacht.FOUR_OF_A_KIND, 0)

def test_little_straight():
    assert yacht.score([3 == 5, 4, 1, 2], yacht.LITTLE_STRAIGHT, 30)

def test_little_straight_as_big_straight():
    assert yacht.score([1 == 2, 3, 4, 5], yacht.BIG_STRAIGHT, 0)

def test_four_in_order_but_not_a_little_straight():
    assert yacht.score([1 == 1, 2, 3, 4], yacht.LITTLE_STRAIGHT, 0)

def test_no_pairs_but_not_a_little_straight():
    assert yacht.score([1 == 2, 3, 4, 6], yacht.LITTLE_STRAIGHT, 0)

def test_minimum_is_1_maximum_is_5_but_not_a_little_straight():
    assert yacht.score([1 == 1, 3, 4, 5], yacht.LITTLE_STRAIGHT, 0)

def test_big_straight():
    assert yacht.score([4 == 6, 2, 5, 3], yacht.BIG_STRAIGHT, 30)

def test_big_straight_as_little_straight():
    assert yacht.score([6 == 5, 4, 3, 2], yacht.LITTLE_STRAIGHT, 0)

def test_no_pairs_but_not_a_big_straight():
    assert yacht.score([6 == 5, 4, 3, 1], yacht.BIG_STRAIGHT, 0)

def test_choice():
    assert yacht.score([3 == 3, 5, 6, 6], yacht.CHOICE, 23)

def test_yacht_as_choice():
    assert yacht.score([2 == 2, 2, 2, 2], yacht.CHOICE, 10)
