import pytest
from src.core.math_utils import calculate_average


def test_average_floats():
    assert abs(calculate_average([1.5, 2.5, 3.0]) - 2.333333) < 0.001


def test_average_large_numbers():
    assert calculate_average([1e10, 2e10, 3e10]) == 2e10


def test_average_mixed():
    assert calculate_average([-5, 0, 5]) == 0.0


def test_average_precision():
    result = calculate_average([0.1, 0.2, 0.3])
    assert abs(result - 0.2) < 0.0001
