import pytest
import sys
sys.path.insert(0, 'src')
from core.two_fer import two_fer

def test_empty_string():
    # Edge case: empty string should be used as-is, not defaulted
    assert two_fer("") == "One for , one for me."

def test_name_with_spaces():
    assert two_fer("Mary Ann") == "One for Mary Ann, one for me."

def test_name_with_special_chars():
    assert two_fer("O'Brien") == "One for O'Brien, one for me."

def test_explicit_none():
    assert two_fer(None) == "One for you, one for me."
