import pytest
import sys
sys.path.insert(0, 'src')
from core.hamming import distance

def test_empty_strands():
    assert distance("", "") == 0

def test_single_identical():
    assert distance("A", "A") == 0

def test_single_different():
    assert distance("G", "T") == 1

def test_complete_hamming_distance():
    assert distance("GGACTGA", "GGACTGA") == 0

def test_small_hamming_distance():
    assert distance("GGACGGATTCTG", "AGGACGGATTCT") == 9

def test_different_lengths_raises():
    with pytest.raises(ValueError) as exc:
        distance("AATG", "AAA")
    assert "equal length" in str(exc.value)
