import pytest
import sys
sys.path.insert(0, 'src')
from core.isogram import is_isogram

def test_empty_string():
    assert is_isogram("") is True

def test_isogram_lowercase():
    assert is_isogram("isogram") is True

def test_not_isogram():
    assert is_isogram("eleven") is False

def test_isogram_with_hyphen():
    assert is_isogram("six-year-old") is True

def test_made_up_with_hyphen():
    assert is_isogram("thumbscrew-japingly") is True

def test_hypothetical_word():
    assert is_isogram("Alphabet") is False

