import pytest
import sys
sys.path.insert(0, 'src')
from core.isogram import is_isogram

def test_duplicated_letter_mixed_case():
    assert is_isogram("alphAbet") is False

def test_isogram_with_spaces():
    assert is_isogram("lumberjacks") is True

def test_isogram_with_multiple_spaces():
    assert is_isogram("the quick brown") is False  # has 'h' and multiple spaces

def test_all_same_letter():
    assert is_isogram("aaaa") is False

def test_only_spaces():
    assert is_isogram("   ") is True

def test_only_hyphens():
    assert is_isogram("---") is True
