def test_partial_garden_garden_with_single_student():
    garden = Garden("RC\nGG")
    assertEqual(
        garden.plants("Alice"), ["Radishes", "Clover", "Grass", "Grass"]
    )

def test_partial_garden_different_garden_with_single_student():
    garden = Garden("VC\nRC")
    assertEqual(
        garden.plants("Alice"), ["Violets", "Clover", "Radishes", "Clover"]
    )

def test_partial_garden_garden_with_two_students():
    garden = Garden("VVCG\nVVRC")
    assertEqual(
        garden.plants("Bob"), ["Clover", "Grass", "Radishes", "Clover"]
    )

def test_partial_garden_second_student_s_garden():
    garden = Garden("VVCCGG\nVVCCGG")
    assert garden.plants("Bob") == ["Clover", "Clover", "Clover", "Clover"]

def test_partial_garden_third_student_s_garden():
