import unittest

from triangle import (
equilateral,
isosceles,
scalene,
)



def test_all_sides_are_equal():
    assertIs(equilateral([2, 2, 2]), True)

def test_any_side_is_unequal():
    assertIs(equilateral([2, 3, 2]), False)

def test_no_sides_are_equal():
    assertIs(equilateral([5, 4, 6]), False)

def test_all_zero_sides_is_not_a_triangle():
    assertIs(equilateral([0, 0, 0]), False)

def test_sides_may_be_floats():
    assertIs(equilateral([0.5, 0.5, 0.5]), True)



def test_last_two_sides_are_equal():
    assertIs(isosceles([3, 4, 4]), True)

def test_first_two_sides_are_equal():
    assertIs(isosceles([4, 4, 3]), True)

def test_first_and_last_sides_are_equal():
    assertIs(isosceles([4, 3, 4]), True)

def test_equilateral_triangles_are_also_isosceles():
    assertIs(isosceles([4, 4, 4]), True)

def test_no_sides_are_equal():
    assertIs(isosceles([2, 3, 4]), False)

def test_first_triangle_inequality_violation():
    assertIs(isosceles([1, 1, 3]), False)

def test_second_triangle_inequality_violation():
    assertIs(isosceles([1, 3, 1]), False)

def test_third_triangle_inequality_violation():
    assertIs(isosceles([3, 1, 1]), False)

def test_sides_may_be_floats():
    assertIs(isosceles([0.5, 0.4, 0.5]), True)



def test_no_sides_are_equal():
    assertIs(scalene([5, 4, 6]), True)

def test_all_sides_are_equal():
    assertIs(scalene([4, 4, 4]), False)

def test_first_and_second_sides_are_equal():
    assertIs(scalene([4, 4, 3]), False)

def test_first_and_third_sides_are_equal():
    assertIs(scalene([3, 4, 3]), False)

def test_second_and_third_sides_are_equal():
    assertIs(scalene([4, 3, 3]), False)

def test_may_not_violate_triangle_inequality():
    assertIs(scalene([7, 3, 2]), False)

def test_sides_may_be_floats():
    assertIs(scalene([0.5, 0.4, 0.6]), True)
